import { Component, OnInit } from '@angular/core';
import { Route, Router } from '@angular/router';
import GiftModel from 'src/app/models/gift-model';
import TargetAudienceModel from 'src/app/models/targetAudience-model';
import { DataService } from 'src/app/services/data.service';

@Component({
  selector: 'app-add',
  templateUrl: './add.component.html',
  styleUrls: ['./add.component.css']
})
export class AddComponent  implements OnInit{

  public targetAudience:TargetAudienceModel[]=[]
  public gift=new GiftModel()

  public constructor(private dataService:DataService,private router:Router){}

   public async ngOnInit() {
    try {

      this.targetAudience=await this.dataService.getAllTargetAudience()

    } catch (error:any) {
      alert(error.message)
    }
  }

  public async send(){
try {
      await this.dataService.addGift(this.gift)
      alert("You add gift")

} catch (error) {

}  }

}
<form (ngSubmit)="send()">

    <select name="targetAudienceId" [(ngModel)]="gift.targetAudienceId" >

        <option selected disabled value="">Select Target Audience</option>
        <option *ngFor="let t of targetAudience" value={{t.targetAudienceId}}>{{t.targetAudienceName}}</option>
    </select>

    <label>Gift Name:</label>
    <input type="text" name="name" [(ngModel)]="gift.name" required>

    <label>Description</label>
    <input type="text" name="description" [(ngModel)]="gift.description" required>

    <label>Price:</label>
    <input type="number" name="price" [(ngModel)]="gift.price" required min="0">

    <label>Discount:</label>
    <input type="number" name="discount" [(ngModel)]="gift.discount" required min="10">

    <button>Add</button>

</form>
